mapper.insert:
INSERT INTO product_qna (
product_idx, member_idx, content
) VALUES (
#{product_idx}, #{member_idx}, #{content}
)

INSERT INTO product_qna (
product_idx, member_idx, content, parent_idx
) VALUES (
#{product_idx}, #{member_idx}, #{content}, #{parent_idx}
)

mapper.update:
UPDATE product_qna
SET
content = #{content}
WHERE
idx = #{idx} and member_idx = #{member_idx}
