mapper.insert:
INSERT INTO announce (
title, content,
file_original, file_system, file_extension
) VALUES (
#{title}, #{content},
#{file_original}, #{file_system}, #{file_extension}
)


  SELECT SEQ_announce_IDX.CURRVAL FROM dual

mapper.select:
SELECT c.* FROM (
SELECT a.*, ROWNUM r
from (
select * from announce b
ORDER BY b.idx DESC
)
a WHERE ROWNUM <= #{end}
) c WHERE c.r >= #{start}

mapper.insert:
INSERT INTO qnaservice (
