#{content}, #{member_idx}, #{category}
)

INSERT INTO qnaservice(
content, parent_idx, isanswered, category, member_idx
) VALUES (
#{content}, #{parent_idx}, #{isanswered}, #{category}, #{member_idx}
)

mapper.update:
